value.string:
"""
 Validate Transaction Lines for source and destination section.
 """

 from Products.DCWorkflow.DCWorkflow import ValidationFailed

 SOURCE, DESTINATION = ('source', 'destination')

 error_message = ''
 transaction = state_change['object']
 N_ = transaction.Base_translateString

 # first of all, validate the transaction itself
 container.validateTransaction(state_change)

 # Get sections.
 source_section = transaction.getSourceSection( portal_type = ['Person', 'Organisation','Category'])
 destination_section = transaction.getDestinationSection(portal_type = ['Person', 'Organisation','Category'])

 source_sum = 0
 destination_sum = 0

 # Check transaction lines
 if transaction.getPortalType() not in ('Balance Transaction',) :
 accounting_transaction_line_list = transaction.contentValues(
 filter={ 'portal_type':
 transaction.getPortalAccountingMovementTypeList()})
 for transaction_line in accounting_transaction_line_list:
 if source_section != destination_section :
 source_quantity = transaction_line.getSourceInventoriatedTotalAssetPrice() or 0
 destination_quantity = transaction_line.getDestinationInventoriatedTotalAssetPrice() or 0
 else :
 destination_quantity = source_quantity = ((transaction_line\
 .getSourceInventoriatedTotalAssetPrice() or 0) + \
 (transaction_line.getDestinationInventoriatedTotalAssetPrice() or 0))

 source_sum += source_quantity
 destination_sum += destination_quantity

 if transaction_line.getSource( portal_type = 'Account') is None and \
 transaction_line.getDestination(portal_type = 'Account') is None and \
 transaction_line.getQuantity() != 0:
 raise ValidationFailed, N_("Action failed: no account defined for line '${line_id}'."
 , mapping = {'line_id': transaction_line.getId()}
 )

 for side in (SOURCE, DESTINATION) :
 if side == SOURCE:
 account = transaction_line.getSourceValue(portal_type = 'Account')
 payment = transaction_line.getSourcePaymentValue()
 third_party_path = transaction_line.getDestinationSection()
 third_party = transaction_line.getDestinationSectionValue()
 else:
 account = transaction_line.getDestinationValue(portal_type = 'Account')
 payment = transaction_line.getDestinationPaymentValue()
 third_party_path = transaction_line.getSourceSection()
 third_party = transaction_line.getSourceSectionValue()

 if account is None:
 continue

 if account.getValidationState() != 'validated':
 raise ValidationFailed, N_("Action failed: account '${account_title}' is not opened."
 , mapping = {'account_title': unicode(account.getTranslatedTitle(), 'utf8')}
 )

 # Test third party related-data
 if account.getAccountTypeId() in ("receivable", "payable"):
 # Test existence
 if third_party_path in (None, ''):
 raise ValidationFailed, N_("Action failed: no third party defined for line '${line}'."
 , mapping = {'line': transaction_line.getId()}
 )
 if third_party not in (None, '') and third_party.getPortalType() in ['Person', 'Organisation']:
 # Test state
 if third_party.getValidationState() != 'validated':
 raise ValidationFailed, N_("Action failed: third party '${third_party_name}' is not validated."
 , mapping = {'third_party_name': unicode(third_party.getTranslatedTitle(), 'utf8')}
 )
 # Test region
 # Note: This test is normally handle by the entity workflow which don't allow validation of entity
 # until region is set. So if the previous condition is not verified, the previous test
 # catch it. We add this redundent test for easy upgrade of previous ERP5 accounting system.
 if third_party.getRegion() in (None, ''):
 raise ValidationFailed, N_("Action failed: third party '${third_party_name}' has no region."
 , mapping = {'third_party_name': unicode(third_party.getTranslatedTitle(), 'utf8')}
 )

 if (side == SOURCE) and account.isMemberOf("account_type/asset/cash/bank"):
 if payment in (None, ""):
 raise ValidationFailed, N_("Action failed: no bank account defined for line '${line}'."
 , mapping = {'line': transaction_line.getId()}
 )
 elif hasattr(payment, 'getPriceCurrency') and \
 payment.getPriceCurrency() is not None and \
 payment.getPriceCurrency() != transaction_line.getResource():
 raise ValidationFailed, N_("Action failed: bank account for line '${line}' "+
 'uses ${bank_account_currency} as default currency.'
 , mapping = { 'line' : transaction_line.getId()
 , 'bank_account_currency': unicode(payment.getPriceCurrencyValue().getReference(), 'utf8')
 }
 )

 if int(source_sum*100) != 0:
 raise ValidationFailed, N_('Action failed: transaction is not balanced for source section.')

 if int(destination_sum*100) != 0:
 raise ValidationFailed, N_('Action failed: transaction is not balanced for destination section.')

 transaction.AccountingTransaction_deleteEmptyLines(redirect=0)
