 define('ace/mode/matching_brace_outdent', ['require', 'exports', 'module' , 'ace/range'], function(require, exports, module) {


 var Range = require("../range").Range;

 var MatchingBraceOutdent = function() {};

 (function() {

 this.checkOutdent = function(line, input) {
 if (! /^\s+$/.test(line))
 return false;

 return /^\s*\}/.test(input);
 };

 this.autoOutdent = function(doc, row) {
 var line = doc.getLine(row);
 var match = line.match(/^(\s*\})/);

 if (!match) return 0;

 var column = match[1].length;
 var openBracePos = doc.findMatchingBracket({row: row, column: column});

 if (!openBracePos || openBracePos.row == row) return 0;

 var indent = this.$getIndent(doc.getLine(openBracePos.row));
 doc.replace(new Range(row, 0, row, column-1), indent);
 };

 this.$getIndent = function(line) {
 return line.match(/^\s*/)[0];
 };

 }).call(MatchingBraceOutdent.prototype);

 exports.MatchingBraceOutdent = MatchingBraceOutdent;
 });

 define('ace/mode/behaviour/cstyle', ['require', 'exports', 'module' , 'ace/lib/oop', 'ace/mode/behaviour', 'ace/token_iterator', 'ace/lib/lang'], function(require, exports, module) {


 var oop = require("../../lib/oop");
 var Behaviour = require("../behaviour").Behaviour;
 var TokenIterator = require("../../token_iterator").TokenIterator;
 var lang = require("../../lib/lang");

 var SAFE_INSERT_IN_TOKENS =
 ["text", "paren.rparen", "punctuation.operator"];
 var SAFE_INSERT_BEFORE_TOKENS =
 ["text", "paren.rparen", "punctuation.operator", "comment"];


 var autoInsertedBrackets = 0;
 var autoInsertedRow = -1;
 var autoInsertedLineEnd = "";
 var maybeInsertedBrackets = 0;
 var maybeInsertedRow = -1;
 var maybeInsertedLineStart = "";
 var maybeInsertedLineEnd = "";

 var CstyleBehaviour = function () {

 CstyleBehaviour.isSaneInsertion = function(editor, session) {
 var cursor = editor.getCursorPosition();
 var iterator = new TokenIterator(session, cursor.row, cursor.column);
 if (!this.$matchTokenType(iterator.getCurrentToken() || "text", SAFE_INSERT_IN_TOKENS)) {
 var iterator2 = new TokenIterator(session, cursor.row, cursor.column + 1);
 if (!this.$matchTokenType(iterator2.getCurrentToken() || "text", SAFE_INSERT_IN_TOKENS))
 return false;
 }
 iterator.stepForward();
 return iterator.getCurrentTokenRow() !== cursor.row ||
 this.$matchTokenType(iterator.getCurrentToken() || "text", SAFE_INSERT_BEFORE_TOKENS);
 };

 CstyleBehaviour.$matchTokenType = function(token, types) {
 return types.indexOf(token.type || token) > -1;
 };

 CstyleBehaviour.recordAutoInsert = function(editor, session, bracket) {
 var cursor = editor.getCursorPosition();
 var line = session.doc.getLine(cursor.row);
 if (!this.isAutoInsertedClosing(cursor, line, autoInsertedLineEnd[0]))
 autoInsertedBrackets = 0;
 autoInsertedRow = cursor.row;
 autoInsertedLineEnd = bracket + line.substr(cursor.column);
 autoInsertedBrackets++;
 };

 CstyleBehaviour.recordMaybeInsert = function(editor, session, bracket) {
 var cursor = editor.getCursorPosition();
 var line = session.doc.getLine(cursor.row);
 if (!this.isMaybeInsertedClosing(cursor, line))
 maybeInsertedBrackets = 0;
 maybeInsertedRow = cursor.row;
 maybeInsertedLineStart = line.substr(0, cursor.column) + bracket;
 maybeInsertedLineEnd = line.substr(cursor.column);
 maybeInsertedBrackets++;
 };

 CstyleBehaviour.isAutoInsertedClosing = function(cursor, line, bracket) {
 return autoInsertedBrackets > 0 &&
 cursor.row === autoInsertedRow &&
 bracket === autoInsertedLineEnd[0] &&
 line.substr(cursor.column) === autoInsertedLineEnd;
 };

 CstyleBehaviour.isMaybeInsertedClosing = function(cursor, line) {
 return maybeInsertedBrackets > 0 &&
 cursor.row === maybeInsertedRow &&
 line.substr(cursor.column) === maybeInsertedLineEnd &&
 line.substr(0, cursor.column) == maybeInsertedLineStart;
 };

 CstyleBehaviour.popAutoInsertedClosing = function() {
 autoInsertedLineEnd = autoInsertedLineEnd.substr(1);
 autoInsertedBrackets--;
 };

 CstyleBehaviour.clearMaybeInsertedClosing = function() {
 maybeInsertedBrackets = 0;
 maybeInsertedRow = -1;
 };

 this.add("braces", "insertion", function (state, action, editor, session, text) {
 var cursor = editor.getCursorPosition();
 var line = session.doc.getLine(cursor.row);
 if (text == '{') {
 var selection = editor.getSelectionRange();
 var selected = session.doc.getTextRange(selection);
 if (selected !== "" && selected !== "{" && editor.getWrapBehavioursEnabled()) {
 return {
 text: '{' + selected + '}',
 selection: false
 };
 } else if (CstyleBehaviour.isSaneInsertion(editor, session)) {
 if (/[\]\}\)]/.test(line[cursor.column])) {
 CstyleBehaviour.recordAutoInsert(editor, session, "}");
 return {
 text: '{}',
 selection: [1, 1]
 };
 } else {
 CstyleBehaviour.recordMaybeInsert(editor, session, "{");
 return {
 text: '{',
 selection: [1, 1]
 };
 }
 }
 } else if (text == '}') {
 var rightChar = line.substring(cursor.column, cursor.column + 1);
 if (rightChar == '}') {
 var matching = session.$findOpeningBracket('}', {column: cursor.column + 1, row: cursor.row});
 if (matching !== null && CstyleBehaviour.isAutoInsertedClosing(cursor, line, text)) {
 CstyleBehaviour.popAutoInsertedClosing();
 return {
 text: '',
 selection: [1, 1]
 };
 }
 }
 } else if (text == "\n" || text == "\r\n") {
 var closing = "";
 if (CstyleBehaviour.isMaybeInsertedClosing(cursor, line)) {
 closing = lang.stringRepeat("}", maybeInsertedBrackets);
 CstyleBehaviour.clearMaybeInsertedClosing();
 }
 var rightChar = line.substring(cursor.column, cursor.column + 1);
 if (rightChar == '}' || closing !== "") {
 var openBracePos = session.findMatchingBracket({row: cursor.row, column: cursor.column}, '}');
 if (!openBracePos)
 return null;

 var indent = this.getNextLineIndent(state, line.substring(0, cursor.column), session.getTabString());
 var next_indent = this.$getIndent(line);

 return {
 text: '\n' + indent + '\n' + next_indent + closing,
 selection: [1, indent.length, 1, indent.length]
 };
 }
 }
 });

 this.add("braces", "deletion", function (state, action, editor, session, range) {
 var selected = session.doc.getTextRange(range);
 if (!range.isMultiLine() && selected == '{') {
 var line = session.doc.getLine(range.start.row);
 var rightChar = line.substring(range.end.column, range.end.column + 1);
 if (rightChar == '}') {
 range.end.column++;
 return range;
 } else {
 maybeInsertedBrackets--;
 }
 }
 });

 this.add("parens", "insertion", function (state, action, editor, session, text) {
 if (text == '(') {
 var selection = editor.getSelectionRange();
 var selected = session.doc.getTextRange(selection);
 if (selected !== "" && editor.getWrapBehavioursEnabled()) {
 return {
 text: '(' + selected + ')',
 selection: false
 };
 } else if (CstyleBehaviour.isSaneInsertion(editor, session)) {
 CstyleBehaviour.recordAutoInsert(editor, session, ")");
 return {
 text: '()',
 selection: [1, 1]
 };
 }
 } else if (text == ')') {
 var cursor = editor.getCursorPosition();
 var line = session.doc.getLine(cursor.row);
 var rightChar = line.substring(cursor.column, cursor.column + 1);
 if (rightChar == ')') {
 var matching = session.$findOpeningBracket(')', {column: cursor.column + 1, row: cursor.row});
 if (matching !== null && CstyleBehaviour.isAutoInsertedClosing(cursor, line, text)) {
 CstyleBehaviour.popAutoInsertedClosing();
 return {
 text: '',
 selection: [1, 1]
 };
 }
 }
 }
 });

 this.add("parens", "deletion", function (state, action, editor, session, range) {
 var selected = session.doc.getTextRange(range);
 if (!range.isMultiLine() && selected == '(') {
 var line = session.doc.getLine(range.start.row);
 var rightChar = line.substring(range.start.column + 1, range.start.column + 2);
 if (rightChar == ')') {
 range.end.column++;
 return range;
 }
 }
 });

 this.add("brackets", "insertion", function (state, action, editor, session, text) {
 if (text == '[') {
 var selection = editor.getSelectionRange();
 var selected = session.doc.getTextRange(selection);
 if (selected !== "" && editor.getWrapBehavioursEnabled()) {
 return {
 text: '[' + selected + ']',
 selection: false
 };
 } else if (CstyleBehaviour.isSaneInsertion(editor, session)) {
 CstyleBehaviour.recordAutoInsert(editor, session, "]");
 return {
 text: '[]',
 selection: [1, 1]
 };
 }
 } else if (text == ']') {
 var cursor = editor.getCursorPosition();
 var line = session.doc.getLine(cursor.row);
 var rightChar = line.substring(cursor.column, cursor.column + 1);
 if (rightChar == ']') {
 var matching = session.$findOpeningBracket(']', {column: cursor.column + 1, row: cursor.row});
 if (matching !== null && CstyleBehaviour.isAutoInsertedClosing(cursor, line, text)) {
 CstyleBehaviour.popAutoInsertedClosing();
 return {
 text: '',
 selection: [1, 1]
 };
 }
 }
 }
 });

 this.add("brackets", "deletion", function (state, action, editor, session, range) {
 var selected = session.doc.getTextRange(range);
 if (!range.isMultiLine() && selected == '[') {
 var line = session.doc.getLine(range.start.row);
 var rightChar = line.substring(range.start.column + 1, range.start.column + 2);
 if (rightChar == ']') {
 range.end.column++;
 return range;
 }
 }
 });

 this.add("string_dquotes", "insertion", function (state, action, editor, session, text) {
 if (text == '"' || text == "'") {
 var quote = text;
 var selection = editor.getSelectionRange();
 var selected = session.doc.getTextRange(selection);
 if (selected !== "" && selected !== "'" && selected != '"' && editor.getWrapBehavioursEnabled()) {
 return {
 text: quote + selected + quote,
 selection: false
 };
 } else {
 var cursor = editor.getCursorPosition();
 var line = session.doc.getLine(cursor.row);
 var leftChar = line.substring(cursor.column-1, cursor.column);
 if (leftChar == '\\') {
 return null;
 }
 var tokens = session.getTokens(selection.start.row);
 var col = 0, token;
 var quotepos = -1; // Track whether we're inside an open quote.

 for (var x = 0; x < tokens.length; x++) {
 token = tokens[x];
 if (token.type == "string") {
 quotepos = -1;
 } else if (quotepos < 0) {
 quotepos = token.value.indexOf(quote);
 }
 if ((token.value.length + col) > selection.start.column) {
 break;
 }
 col += tokens[x].value.length;
 }
 if (!token || (quotepos < 0 && token.type !== "comment" && (token.type !== "string" || ((selection.start.column !== token.value.length+col-1) && token.value.lastIndexOf(quote) === token.value.length-1)))) {
 if (!CstyleBehaviour.isSaneInsertion(editor, session))
 return;
 return {
 text: quote + quote,
 selection: [1,1]
 };
 } else if (token && token.type === "string") {
 var rightChar = line.substring(cursor.column, cursor.column + 1);
 if (rightChar == quote) {
 return {
 text: '',
 selection: [1, 1]
 };
 }
 }
 }
 }
 });

 this.add("string_dquotes", "deletion", function (state, action, editor, session, range) {
 var selected = session.doc.getTextRange(range);
 if (!range.isMultiLine() && (selected == '"' || selected == "'")) {
 var line = session.doc.getLine(range.start.row);
 var rightChar = line.substring(range.start.column + 1, range.start.column + 2);
 if (rightChar == selected) {
 range.end.column++;
 return range;
 }
 }
 });

 };

 oop.inherits(CstyleBehaviour, Behaviour);

 exports.CstyleBehaviour = CstyleBehaviour;
 });

 define('ace/mode/folding/cstyle', ['require', 'exports', 'module' , 'ace/lib/oop', 'ace/range', 'ace/mode/folding/fold_mode'], function(require, exports, module) {


 var oop = require("../../lib/oop");
 var Range = require("../../range").Range;
 var BaseFoldMode = require("./fold_mode").FoldMode;

 var FoldMode = exports.FoldMode = function(commentRegex) {
 if (commentRegex) {
 this.foldingStartMarker = new RegExp(
 this.foldingStartMarker.source.replace(/\|[^|]*?$/, "|" + commentRegex.start)
 );
 this.foldingStopMarker = new RegExp(
 this.foldingStopMarker.source.replace(/\|[^|]*?$/, "|" + commentRegex.end)
 );
 }
 };
 oop.inherits(FoldMode, BaseFoldMode);

 (function() {

 this.foldingStartMarker = /(\{|\[)[^\}\]]*$|^\s*(\/\*)/;
 this.foldingStopMarker = /^[^\[\{]*(\}|\])|^[\s\*]*(\*\/)/;

 this.getFoldWidgetRange = function(session, foldStyle, row) {
 var line = session.getLine(row);
 var match = line.match(this.foldingStartMarker);
 if (match) {
 var i = match.index;

 if (match[1])
 return this.openingBracketBlock(session, match[1], row, i);

 return session.getCommentFoldRange(row, i + match[0].length, 1);
 }

 if (foldStyle !== "markbeginend")
 return;

 var match = line.match(this.foldingStopMarker);
 if (match) {
 var i = match.index + match[0].length;

 if (match[1])
 return this.closingBracketBlock(session, match[1], row, i);

 return session.getCommentFoldRange(row, i, -1);
 }
 };

 }).call(FoldMode.prototype);

 });

 define('ace/mode/glsl_highlight_rules', ['require', 'exports', 'module' , 'ace/lib/oop', 'ace/mode/c_cpp_highlight_rules'], function(require, exports, module) {


 var oop = require("../lib/oop");
 var c_cppHighlightRules = require("./c_cpp_highlight_rules").c_cppHighlightRules;

 var glslHighlightRules = function() {

 var keywords = (
 "attribute|const|uniform|varying|break|continue|do|for|while|" +
 "if|else|in|out|inout|float|int|void|bool|true|false|" +
 "lowp|mediump|highp|precision|invariant|discard|return|mat2|mat3|" +
 "mat4|vec2|vec3|vec4|ivec2|ivec3|ivec4|bvec2|bvec3|bvec4|sampler2D|" +
 "samplerCube|struct"
 );

 var buildinConstants = (
 "radians|degrees|sin|cos|tan|asin|acos|atan|pow|" +
 "exp|log|exp2|log2|sqrt|inversesqrt|abs|sign|floor|ceil|fract|mod|" +
 "min|max|clamp|mix|step|smoothstep|length|distance|dot|cross|" +
 "normalize|faceforward|reflect|refract|matrixCompMult|lessThan|" +
 "lessThanEqual|greaterThan|greaterThanEqual|equal|notEqual|any|all|" +
 "not|dFdx|dFdy|fwidth|texture2D|texture2DProj|texture2DLod|" +
 "texture2DProjLod|textureCube|textureCubeLod|" +
 "gl_MaxVertexAttribs|gl_MaxVertexUniformVectors|gl_MaxVaryingVectors|" +
 "gl_MaxVertexTextureImageUnits|gl_MaxCombinedTextureImageUnits|" +
 "gl_MaxTextureImageUnits|gl_MaxFragmentUniformVectors|gl_MaxDrawBuffers|" +
 "gl_DepthRangeParameters|gl_DepthRange|" +
 "gl_Position|gl_PointSize|" +
 "gl_FragCoord|gl_FrontFacing|gl_PointCoord|gl_FragColor|gl_FragData"
 );

 var keywordMapper = this.createKeywordMapper({
 "variable.language": "this",
 "keyword": keywords,
 "constant.language": buildinConstants
 }, "identifier");

 this.$rules = new c_cppHighlightRules().$rules;
 this.$rules.start.forEach(function(rule) {
 if (typeof rule.token == "function")
 rule.token = keywordMapper;
 })
 };

 oop.inherits(glslHighlightRules, c_cppHighlightRules);

 exports.glslHighlightRules = glslHighlightRules;
 });
