value.string:
from email.utils import formataddr
 portal = context.getPortalObject()

 if body is None:
 body = context.getTextContent() #XXX This does not support structured text format.

 # Subject
 if subject is None:
 subject = context.getTitle()

 # From
 if from_url is None:
 sender = context.getSourceValue()
 from_url = formataddr((sender.getTitle(), sender.getDefaultEmailText()))

 # Return-Path
 if reply_url is None:
 reply_url = portal.portal_preferences.getPreferredEventSenderEmail()
 additional_headers = None
 if reply_url:
 additional_headers = {'Return-Path':reply_url}

 # To (multiple)
 to_url_list = []
 if to_url is None:
 for recipient in context.getDestinationValueList():
 email = recipient.getDefaultEmailText()
 if email:
 to_url_list.append(formataddr((recipient.getTitle(), email)))
 else:
 raise ValueError, 'Recipient %s has no defined email' % recipient
 elif same_type(to_url, ''):
 to_url_list.append(to_url)

 # Attachments
 if attachment_list is None:
 attachment_list = []
 document_type_list = list(context.getPortalEmbeddedDocumentTypeList()) + list(context.getPortalDocumentTypeList())
 for attachment in context.getAggregateValueList(portal_type=document_type_list):
 mime_type, content = attachment.getMimeTypeAndContent()
 name = attachment.getFilename()

 attachment_list.append({'mime_type':mime_type,
 'content':content,
 'name':name})

 # Allow to embed image in HTML by setting the relative URL of the image with FCKeditor
 attachment_url = attachment.getRelativeUrl()
 body = body.replace('src="%s"' % attachment_url, 'src="cid:%s"' % ''.join(['%s' % ord(i) for i in name]))

 # Build message per destination
 mail_message = None
 if context.getContentType() == 'text/html':
 mail_template = context.Event_viewHtmlMimeMessage
 else:
 mail_template = context.Event_viewMimeMessage

 for to_url in to_url_list:
 multipart = mail_template.as_message(mfrom=from_url,
 mto=to_url,
 subject=subject,
 body=body,
 encoding='utf-8')
 for attachment_dict in attachment_list:
 multipart.add_file(data=attachment_dict['content'],
 content_type=attachment_dict['mime_type'],
 filename=attachment_dict['name'])
 mail_message = str(multipart)
 context.activate(activity='SQLQueue').sendMailHostMessage(mail_message)

 context.setData(mail_message)

 # Transit event workflow
 if context.getTypeInfo() is not None:
 for transition_id in 'plan', 'order', 'start':
 if portal.portal_workflow.isTransitionPossible(context, transition_id):
 getattr(context, transition_id)()

 if download:
 return mail_message
