value.string:
portal = context.getPortalObject()
 portal_preferences = portal.portal_preferences
 system_preference = None
 clear_cache = 0
 conversion_check = False

 if "conversion_server" in kw:
 conversion_server_address, conversion_server_port = kw["conversion_server"].split(":")

 def getActiveSystemPreference():
 system_preference = portal_preferences.getActiveSystemPreference()
 if system_preference is None:
 system_preference = portal_preferences.newContent(
 portal_type="System Preference", 
 title="Automatically Created.")
 system_preference.enable()
 return system_preference

 if portal_preferences.getPreferredOoodocServerAddress() != conversion_server_address:
 system_preference = getActiveSystemPreference()
 system_preference.setPreferredOoodocServerAddress(conversion_server_address)
 clear_cache = 1

 if int(portal_preferences.getPreferredOoodocServerPortNumber()) != int(conversion_server_port):
 if system_preference is None:
 system_preference = getActiveSystemPreference()
 system_preference.setPreferredOoodocServerPortNumber(int(conversion_server_port))
 clear_cache = 1

 if "memcached_server" in kw:
 default_memcached_plugin = getattr(portal.portal_memcached, "default_memcached_plugin", None)
 if default_memcached_plugin.getUrlString() != kw["memcached_server"]:
 default_memcached_plugin.setUrlString(kw["memcached_server"])

 if "kumo_server" in kw:
 persistent_memcached_plugin = getattr(portal.portal_memcached, "persistent_memcached_plugin", None)
 if persistent_memcached_plugin is not None:
 if persistent_memcached_plugin.getUrlString() != kw["kumo_server"]:
 persistent_memcached_plugin.setUrlString(kw["kumo_server"])


 if clear_cache:
 portal.portal_caches.clearAllCache()

 if "conversion_server" in kw:
 conversion_check = (portal_preferences.getPreferredOoodocServerAddress() == conversion_server_address) and \
 (int(portal_preferences.getPreferredOoodocServerPortNumber()) == int(conversion_server_port))

 return conversion_check and \
 default_memcached_plugin.getUrlString() == kw.get("memcached_server") and \
 persistent_memcached_plugin is not None and \
 persistent_memcached_plugin.getUrlString() == kw.get("kumo_server")
