from Products.ERP5Subversion.SubversionClient import SubversionSSLTrustError
 from Products.ERP5Subversion.SubversionClient import SubversionLoginError
 from Products.ERP5Type.Message import Message
 from ZTUtils import make_query
 request = container.REQUEST

 N_ = lambda msg: Message('erp5_ui', msg)

 # XXX: To be compatible with commit from diff view
 if same_type(added, []):
 added = ','.join(added)
 if same_type(modified, []):
 modified = ','.join(modified)
 if same_type(removed, []):
 removed = ','.join(removed)

 commit_non_recurs = added
 if commit_non_recurs != '' and commit_non_recurs != 'none':
 if modified != '' and modified != 'none':
 commit_non_recurs = commit_non_recurs+','+modified 
 else:
 if modified != '' and modified != 'none':
 commit_non_recurs = modified

 if commit_non_recurs != '' and commit_non_recurs != 'none':
 commit_non_recurs = commit_non_recurs.split(',')
 if removed != '' and removed != 'none':
 commit_recurs = context.getPortalObject()["portal_subversion"].cleanChildrenInList(removed.split(','))
 else:
 commit_recurs = removed

 if changelog is None :
 request.set('portal_status_message', N_('Please Set a ChangeLog Message'))
 request.set('cancel_url', context.absolute_url() + '/BusinessTemplate_viewSvnStatus?do_extract=False&portal_status_message=Commit%20cancelled.')
 return context.asContext(added=added, modified=modified, removed=removed).BusinessTemplate_viewSvnChangelog()

 if changelog.strip() == '' :
 request.set('portal_status_message', N_("Error: ChangeLog Message can't be Empty"))
 request.set('cancel_url', context.absolute_url() + '/BusinessTemplate_viewSvnStatus?do_extract=False&portal_status_message=Commit%20cancelled.')
 return context.asContext(added=added, modified=modified, removed=removed).BusinessTemplate_viewSvnChangelog()

 try:
 if commit_non_recurs != 'none' and commit_non_recurs != '':
 context.getPortalObject()["portal_subversion"].checkin(path=commit_non_recurs, business_template=context, recurse=False, log_message=changelog)
 if commit_recurs != 'none' and commit_recurs != '':
 context.getPortalObject()["portal_subversion"].checkin(path=commit_recurs, business_template=context, recurse=True, log_message=changelog)
 except SubversionSSLTrustError, error:
 request.set('portal_status_message', 'SSL Certificate was not recognized')
 request.set('cancel_url', context.absolute_url() + '/BusinessTemplate_viewSvnStatus?do_extract=False&portal_status_message=Commit%20cancelled.')
 return context.asContext(added=added, modified=modified, removed=removed, changelog=changelog, trust_dict = error.getTrustDict(), caller='commit').BusinessTemplate_viewSvnSSLTrust()
 except SubversionLoginError, error1 :
 request.set('portal_status_message', 'Server needs authentication, no cookie found')
 request.set('cancel_url', context.absolute_url() + '/BusinessTemplate_viewSvnStatus?do_extract=False&portal_status_message=Commit%20cancelled.')
 return context.asContext(added=added, modified=modified, removed=removed, changelog=changelog, caller='commit', realm = error1.getRealm(), username = context.getPortalObject()["portal_subversion"].getPreferredUsername()).BusinessTemplate_viewSvnLogin()

 return request.RESPONSE.redirect('%s/view?%s' % (
 context.absolute_url(),
 make_query(portal_status_message=N_('Files commited successfully'))))
