from Products.ERP5Subversion.SubversionClient import SubversionSSLTrustError, SubversionLoginError

 if do_extract == 'True':
 context.getPortalObject().portal_subversion.extractBT(context)

 context.REQUEST.RESPONSE.setHeader('Content-Type', 'text/xml; charset=utf-8')

 try:
 root = context.getPortalObject().portal_subversion.getModifiedTree(context, show_unmodified)
 except SubversionSSLTrustError, error:
 context.REQUEST.set('portal_status_message', 'SSL Certificate was not recognized')
 return context.asContext(trust_dict = error.getTrustDict(), caller='tree').BusinessTemplate_viewSvnSSLTrust()
 except SubversionLoginError, error1 :
 context.REQUEST.set('portal_status_message', 'Server needs authentication, no cookie found')
 return context.asContext(caller='tree', realm = error1.getRealm(), username = context.getPortalObject()["portal_subversion"].getPreferredUsername()).BusinessTemplate_viewSvnLogin()


 if not root:
 return '''<?xml version='1.0' encoding='UTF-8'?>
 <tree id='0'></tree>'''

 return context.getPortalObject().portal_subversion.treeToXML(root, context)
