value.string:
# XXX: ERP5VCS_doCreateJavaScriptStatus should send lists
 if isinstance(added, basestring):
 added = added != 'none' and filter(None, added.split(',')) or ()
 if isinstance(modified, basestring):
 modified = modified != 'none' and filter(None, modified.split(',')) or ''
 if isinstance(removed, basestring):
 removed = removed != 'none' and filter(None, removed.split(',')) or ()

 if changelog is None or not changelog.strip():
 request = container.REQUEST
 from Products.ERP5Type.Message import translateString
 error_msg = "Please set a ChangeLog message."
 request.set('portal_status_message', translateString(error_msg))
 request.set('cancel_url', context.absolute_url() +
 '/BusinessTemplate_viewVcsStatus?do_extract:int=0'
 '&portal_status_message=Commit%20cancelled.')
 context = context.asContext(added=added, modified=modified, removed=removed)
 return context.BusinessTemplate_viewVcsChangelog()

 try:
 return context.getVcsTool().commit(changelog, added, modified, removed)
 except Exception, error:
 return context.BusinessTemplate_handleException(error, script.id)
