from Products.ERP5Type.Message import translateString
 item_list = []
 request = context.REQUEST
 total_quantity = 0.0
 type = request.get("field_your_type") or 'Item'
 if context.getPortalType()=='Purchase Packing List Line':
 cell_portal_type = 'Purchase Packing List Cell'
 elif context.getPortalType()=='Purchase Order Line' :
 cell_portal_type = 'Purchase Order Cell'
 elif context.getPortalType()=='Sale Packing List Line':
 cell_portal_type = 'Sale Packing List Cell'
 elif context.getPortalType()== 'Internal Packing List Line':
 cell_portal_type = 'Internal Packing List Cell'

 movement_cell_list = context.contentValues(portal_type=cell_portal_type)
 base_id = 'movement'

 for line in kw.get('listbox'):

 if line.has_key('listbox_key') and (line['title'] or line['reference'] not in ('', None)):

 if line['quantity'] in ('',None):
 msg = translateString("Please Define Quantity for Item Defined On Line ${line_id}",
 mapping={'line_id': line['listbox_key']})
 raise NotImplementedError(msg)

 module = context.getDefaultModule(type)
 item = module.newContent(portal_type=type,
 title=line['title'],
 reference=line['reference'],
 quantity=line['quantity'],
 quantity_unit=context.getQuantityUnit())
 item.validate()
 item_list.append(item) 


 if line.has_key("variation_category_list") and line['variation_category_list'] not in('',[]):
 cell_found = None
 cell_found = context.getCell(base_id='movement', *line["variation_category_list"])
 if cell_found is not None:
 movement_to_update = cell_found
 else:
 if not context.hasInRange(base_id='movement', *line["variation_category_list"]):
 # update line variation category list, if not already containing this one
 variation_category_list = context.getVariationCategoryList()
 for variation in line["variation_category_list"]:
 if variation not in variation_category_list:
 variation_category_list.append(variation)
 context.setVariationCategoryList(variation_category_list)
 movement_to_update = context.newCell(base_id='movement',
 portal_type=cell_portal_type,
 *line["variation_category_list"])
 movement_to_update.edit(mapped_value_property_list=('quantity', 'price'),
 variation_category_list=line["variation_category_list"],)

 else:
 # no variation, we'll update the line itself
 movement_to_update = context

 if item not in movement_to_update.getAggregateValueList():
 movement_to_update.setAggregateValueSet(
 movement_to_update.getAggregateValueList() + [item])


 update_quantity = not context.Movement_isQuantityEditable()
 if update_quantity:
 if context.isMovement():
 movement_list = context,
 else:
 movement_list = context.getCellValueList(base_id='movement')
 for movement in movement_list:
 quantity = 0
 item_list = movement.getAggregateValueList()
 for item in item_list:
 if item.getQuantityUnit() != movement.getQuantityUnit():
 if len(item_list) > 1:
 raise NotImplementedError(
 'Quantity unit from the movement differs from quantity'
 ' unit on the item')
 quantity += item.getQuantity()
 movement.setQuantity(quantity)

 return context.Base_redirect(form_id, keep_items=dict(
 portal_status_message=translateString('Items created')))
