 function loadPadFromServer(pad_relative_url, selected_pad_dom_id, mode){
 /* Load Pad from server */
 // XXX: show some animation ?
 getElement("loading-wrapper").style.display="block";

 d = MochiKit.Async.loadJSONDoc('KnowledgePag_getPadAsJSON', 
 {'pad_relative_url':pad_relative_url,
 'mode':mode});
 //d.addCallback(handleServerSuccess);
 d.addCallbacks(handleServerSuccess, metadataFetchFailed);

 // set old pad to not selected
 pads_container = getElement("tabs");
 old_selected_pad = getFirstElementByTagAndClassName("li", "tab_selected", pads_container);
 old_selected_pad.setAttribute("class", "tab");
 pad_actions = getFirstElementByTagAndClassName("div", "pad-actions", old_selected_pad);
 pad_actions.style.display="none";

 // set new selected pad class 
 new_selected_pad = getElement(selected_pad_dom_id);
 new_selected_pad.setAttribute("class", "tab tab_selected");

 // enable "settings" for this pad and hide instant switch
 pad_actions = getFirstElementByTagAndClassName("div", "pad-actions", new_selected_pad);
 pad_actions.style.display="block";

 // set new active pad
 active_knowledge_pad_relative_url = pad_relative_url;

 function metadataFetchFailed(meta){}

 function handleServerSuccess(meta){
 body = meta.body
 javascript = meta.javascript
 body_element = getElement('pad-body-wrapper');
 body_element.innerHTML = body;
 // init new Pad
 initialize();
 // execute JS code provided by server
 eval(javascript);
 // give some timeout as we can be sometimes two fast loading a tab
 setTimeout("getElement('loading-wrapper').style.display='none';", 250 );
 }
 }

 function addNewPad(cancel_url, knowledge_pad_relative_url, mode){
 // if it's first time we switch set active_knowledge_pad_relative_url
 if(active_knowledge_pad_relative_url==''){
 active_knowledge_pad_relative_url = knowledge_pad_relative_url;
 }
 // redirect to add gadget dialog
 window.location="Base_viewGadgetListDialog?reset=1&cancel_url="+cancel_url+"&active_pad_relative_url="+active_knowledge_pad_relative_url+"&mode="+mode; 
 }

 function addPadOnServerOnEnter(event, create_url, mode, cancel_url){
 /* Catch and submit form when ENTER is pressed */
 if(event.keyCode == 13){
 addPadOnServer(create_url, mode, cancel_url);
 }
 }

 function addPadOnServer(create_url,
 mode,
 cancel_url){
 /* add pad on server and set cookie as its relative url this way we 
 can show it immediately in UI */
 pad_title = getElement('new_pad_title');
 pad_title_value = pad_title.value
 d = doSimpleXMLHttpRequest(
 create_url, 
 {'pad_title': pad_title_value,
 'mode': mode});
 d.addCallbacks(handleServerSuccess, handleServerError);
 function handleServerSuccess(res){
 window.location = cancel_url+'?active_pad_url='+res.responseText;
 };
 };

 function removeKnowledgePadFromServer(knowledge_pad_relative_url, mode){
 /* remove pad from server*/
 if (is_knowledge_template_used){
 showCreateDefaultKnowledgePadWarningMessage();}
 else{
 var user_choice = true;
 user_choice = confirm("Are you sure you want to remove this pad from your home?");
 if (user_choice==true){
 location.href='ERP5Site_deleteKnowledgePad?knowledge_pad_relative_url=' + knowledge_pad_relative_url+'&mode='+mode;}
 }
 }

 function renameKnowledgePadToServerOnEnter(event){
 if(event.keyCode == 13){
 renameKnowledgePadToServer();
 return false;
 }
 return true;
 }

 function renameKnowledgePadToServer(){
 if (is_knowledge_template_used){
 showCreateDefaultKnowledgePadWarningMessage();}
 else{
 // rename it locally and update server asynchonously

 // --title_id, input_id, knowledge_pad_relative_url

 title_element = getElement(active_knowledge_pad_title_dom_id)
 input_element = getElement("new_knowledge_pad_title")
 var knowledge_pad_title = input_element.value;
 title_element.innerHTML = knowledge_pad_title;
 doSimpleXMLHttpRequest("ERP5Site_renameKnowledgePad", 
 {knowledge_pad_relative_url: active_knowledge_pad_relative_url,
 knowledge_pad_title: knowledge_pad_title});
 }
 //anyway toggle show dialog
 toggle("rename_tab_dialog");
 }

 function toggleElementById(dom_id){
 /* Toggle element by its Id. */
 MochiKit.Visual.toggle(dom_id);
 }

 function initialize(){
 // define sortable columns
 if (is_knowledge_template_used==0){
 // allow drag and drop only if we are dealing with a pad we can modify
 var sortables = getElementsByTagAndClassName('div', 'portal-column');
 forEach(sortables, function (item) {
 // eliminate undraggable columns by checking exact match
 if (item.className=='portal-column'){
 MochiKit.Sortable.create(item.id, {"constraint" : false,
 "containment" : sortables,
 "handle": "handle",
 "only": "block",
 "tag": "div",
 "scroll": "true",
 "hoverclass": "block-hover",
 "dropOnEmpty": "true",
 "onUpdate": updateServerBoxColumnLayout,
 "starteffect" : MochiKit.Base.noop,
 "endeffect" : MochiKit.Base.noop
 });
 };
 })}

 // enable show/hide tabs
 tabs = getElement('tabs');
 tabs_switcher = getElement('tabs_switcher');
 add_gadget = getElement('add_new_gadget_link')
 if(tabs_switcher){
 connect(tabs_switcher, 'onclick', function (){
 var is_tabs_visible=0;
 if(tabs.style.display!='block'){
 is_tabs_visible=1;
 getElement("tab_switcher_visible").style.display = "block";
 getElement("tab_switcher_hidden").style.display = "none";
 add_gadget.className = "border_bottom1px";
 tabs_switcher.className = "border_bottom1px";
 }
 else{
 getElement("tab_switcher_hidden").style.display = "block";
 getElement("tab_switcher_visible").style.display = "none";
 add_gadget.className = "border_bottom0px";
 tabs_switcher.className = "border_bottom0px";
 }
 toggle(tabs);
 createCookie("is_tabs_visible", is_tabs_visible, 365);
 });}

 // for each box (gadget) add respective event handlers
 var boxes = getElementsByTagAndClassName('div', 'block');
 forEach(boxes, function (box) {
 var edit = getFirstElementByTagAndClassName('a',
 'block-edit-form', box);
 var edit_form = getFirstElementByTagAndClassName('div', 
 'edit-form', box);
 var remove = getFirstElementByTagAndClassName('a', 
 'block-remove', box);
 var minimize = getFirstElementByTagAndClassName('a', 
 'block-minimize', box);
 var minimize_wrapper = getFirstElementByTagAndClassName('div', 
 'minimize_wrapper', box);
 if(minimize){
 connect(minimize, 'onclick', function (){
 if (is_knowledge_template_used){
 showCreateDefaultKnowledgePadWarningMessage();}
 else{
 toggle(minimize_wrapper);
 doSimpleXMLHttpRequest("KnowledgeBox_toggleVisibility", 
 {box_relative_url: box.id});}});
 }
 if(edit){
 connect(edit, 'onclick', function (){
 if (is_knowledge_template_used){
 showCreateDefaultKnowledgePadWarningMessage();}
 else{toggle(edit_form);}});
 }

 if(remove){
 connect(remove, 'onclick', function (){
 if (is_knowledge_template_used){
 showCreateDefaultKnowledgePadWarningMessage();}
 else{
 user_choice = confirm("Are you sure you want to remove this gadget from your personalized page?");
 if (user_choice==true){
 toggle(box);
 doSimpleXMLHttpRequest("KnowledgePad_deleteBox", 
 {box_relative_url: box.id});}}});
 }
 });
 }

 // call function after load of document
 MochiKit.DOM.addLoadEvent(initialize);
