 # 0.5 is too small and can crash pdf generation
 min_column_width = 1

 max_line_len = {}
 number = (int, float, long)
 for listboxline in listboxline_list:
 line_width = 0
 col_count = 0
 for column_id, column_property in listboxline.getColumnItemList():
 if isinstance(column_property, number):
 string_property = "%s+++" % (column_property)
 elif isinstance(column_property, DateTime):
 string_property = column_property.strftime('%Y/%m/%d++')
 else:
 string_property = str(column_property or '')
 if listboxline.isStatLine():
 string_property = "%s+++++" % string_property
 elif listboxline.isTitleLine():
 string_property = str(N_(string_property))
 line_width += len(string_property)
 new_column_max_len = max(len(string_property), max_line_len.get(column_id, 0))
 update_dict = {}
 update_dict[column_id] = new_column_max_len
 max_line_len.update(update_dict)

 total_max_len = float(sum(max_line_len.values()))
 result = {}
 for column_id, max_len in max_line_len.items() :
 result[column_id] = max( (max(max_len, 1) / total_max_len) * frame_width, min_column_width)

 return result
