"""
 Create an image object and upload the image if necessary
 """

 translateString = context.Base_translateString

 msg = 'Slide created.'

 if edit_mode == 1:
 msg = 'Slide updated.'

 def createImage(image_id):
 return context.newContent(portal_type = 'Embedded File',
 title = image_id,
 id = image_id,
 reference = image_id,
 short_title = image_id)

 if slide_type in ['Screenshot', 'Illustration']:
 if edit_mode:
 if image_id is None or not image_id:
 image = createImage(context.TestPage_getNextImageID(chapter_title, slide_type))
 else:
 try:
 image = context.restrictedTraverse(context.getPath() + '/' + image_id)
 except:
 if not batch_mode:
 image_id = context.TestPage_getNextImageID(chapter_title, slide_type)
 image = createImage(image_id)
 else:
 if not batch_mode:
 image_id = context.TestPage_getNextImageID(chapter_title, slide_type)
 image = createImage(image_id)
 msg += ' Image ' + image_id + ' created.'

 if not(file is None or not file):
 image.edit(file=file, 
 data=file.read(), 
 filename=file.filename)

 msg += ' Image uploaded.'

 if batch_mode:
 return image

 form_id = context.REQUEST.get('dialog_id', None)
 context.Base_redirect(form_id,
 keep_items = dict(portal_status_message=translateString(msg)))
