            if (gadget.props.filling_count == 0){\n
              document.querySelector(\'#dialog_submit_button\').click();\n
            }\n
          }\n
        );\n
    })\n
    .declareService(function () {\n
      var gadget = this;\n
      console.log("location",location);\n
      // WARNING: This considers that the view (in this case,\n
