import datetime
 import json
 from DateTime import DateTime

 response = container.REQUEST.RESPONSE

 try:
 results = context.manage_test(query)
 data = [ results.names() ]
 data.extend(results.tuples())
 except Exception, e:
 response.setStatus(500)
 try:
 response.write(str(e[1]))
 except Exception, _:
 response.write(str(e))
 return

 # handle non JSON serializable data
 new_data = [data[0]]
 for line in data[1:]:
 new_line = []
 for v in line:
 if isinstance(v, DateTime):
 v = v.ISO()
 if isinstance(v, datetime.datetime):
 v = v.isoformat()
 new_line.append(v)
 new_data.append(new_line)

 response.setHeader('Content-Type', 'application/json')
 return json.dumps(new_data, indent=2)






 import datetime
 import json
 from DateTime import DateTime

 response = container.REQUEST.RESPONSE

 try:
 results = context.manage_test(query)
 data = [ results.names() ]
 data.extend(results.dictionaries())
 except Exception, e:
 response.setStatus(500)
 try:
 response.write(str(e[1]))
 except Exception, _:
 response.write(str(e))
 return

 # handle non JSON serializable data
 new_data = [data[0]]
 for line in data[1:]:
 new_line = {}
 for k, v in line.items():
 if isinstance(v, DateTime):
 v = v.ISO()
 if isinstance(v, datetime.datetime):
 v = v.isoformat()
 line[k] = v#new_line.append(v)
 new_data.append(line)

 response.setHeader('Content-Type', 'application/json')
 return json.dumps(new_data, indent=2)
