from Products.ERP5Type.Utils import UpperCase
 from ZODB.POSException import ConflictError
 # XXX Ã refaire pour optimisation
 method = context.z_catalog_syncml_document_list
 property_list = method.arguments_src.split()
 parameter_dict = {}
 failed_path_list = []
 restrictedTraverse = context.getPortalObject().restrictedTraverse
 subscription = restrictedTraverse(subscription_path)
 for path in path_list:
 obj = restrictedTraverse(path, None)
 try:
 tmp_dict = {}
 for property in property_list:
 if subscription_path:
 if property == "gid": 
 # Call specific method to compute GID on source data
 value = subscription.getGidFromObject(obj)
 elif property == "data":
 value = subscription.getDataFromDocument(obj)
 else:
 value = getattr(obj, 'get%s' % UpperCase(property))() 
 else:
 if property == "gid":
 # Signature GID is defined as its ID
 value = getattr(obj, 'getId')()
 else:
 value = getattr(obj, 'get%s' % UpperCase(property))()
 tmp_dict[property] = value
 except ConflictError:
 raise
 except Exception, e:
 raise
 exception = e
 failed_path_list.append(path)
 else:
 for property, value in tmp_dict.items():
 parameter_dict.setdefault(property, []).append(value)

 if len(failed_path_list):
 if len(parameter_dict):
 # reregister activity for failed objects only
 activate_kw.update(activity='SQLQueue', priority=5)
 context.activate(**activate_kw).SQLCatalog_indexSyncMLSignatureList(path_list=failed_path_list)
 else:
 # if all objects are failed one, just raise an exception to avoid infinite loop.
 raise AttributeError, 'exception %r raised in indexing %r' % (exception, failed_path_list)

 if parameter_dict:
 return method(**parameter_dict)
