 /*global console, window, document, rJS, RSVP, $ */
 (function (window, document, rJS, promiseEventListener) {
 "use strict";

 var DEFAULT_GADGET_URL = "interface_bootstrap3_wysiwyg.html";

 function declareSubGadget(gadget, sub_gadget_url) {
 return gadget.declareGadget(sub_gadget_url, {
 sandbox: 'iframe',
 element: gadget.props.content_element.querySelector("div.ui-content"),
 scope: "sub_gadget"
 });
 }
 function getAvailableSubGadgetList(gadget) {
 var main_gadget = gadget.props.content_element.querySelector('form.main-gadget-select-form'),
 gadget_option_list = main_gadget.main_gadget_selector.querySelectorAll('option'),
 gadget_list = [],
 i;
 for(i = 0; i < gadget_option_list.length; i += 1) {
 gadget_list.push({
 url: gadget_option_list[i].value,
 display_name: gadget_option_list[i].innerHTML
 });
 }
 return gadget_list;
 }
 function filterSimilarSubGadgetList(gadget, sub_gadget_url) {
 var available_subgadget_list,
 interface_url_list;
 return RSVP.Queue()
 .push(function() {
 return gadget.getVerifyGadget(sub_gadget_url);
 })
 .push(function(sub_gadget) {
 return RSVP.all([
 gadget.getDeclaredGadgetInterfaceList(sub_gadget),
 getAvailableSubGadgetList(gadget)
 ]);
 })
 .push(function(result_data) {
 var item,
 status_list = [];
 interface_url_list = result_data[0],
 available_subgadget_list = result_data[1];
 for(item in available_subgadget_list) {
 status_list.push(gadget.verifyGadgetInterfaceImplementation(available_subgadget_list[item].url, interface_url_list));
 }
 return RSVP.all(status_list);
 })
 .push(function(result_list) {
 return [available_subgadget_list, result_list];
 });
 }
 function updateSimilarGadgetOptionList(gadget, sub_gadget_url) {
 return RSVP.Queue()
 .push(function() {
 return filterSimilarSubGadgetList(gadget, sub_gadget_url);
 })
 .push(function(filtered_list) {
 var select = gadget.props.content_element.querySelector('form.similar-gadget-select-form').similar_gadget_selector,
 i;
 select.innerHTML = "";
 for(i = 0; i < filtered_list[1].length; i += 1) {
 if(filtered_list[1][i]) {
 var option = document.createElement("option");
 option.text = filtered_list[0][i].display_name;
 option.value = filtered_list[0][i].url;
 select.appendChild(option);
 }
 }
 select.value = filtered_list[0][0].url;
 return;
 });
 }
 function gadgetSwitchHandler(gadget) {
 var current_state = "",
 selected_gadget,
 submit_source;
 return new RSVP.Queue()
 .push(function() {
 return RSVP.any([
 promiseEventListener(
 gadget.props.content_element.querySelector('form.main-gadget-select-form'),
 'submit',
 false
 ),
 promiseEventListener(
 gadget.props.content_element.querySelector('form.similar-gadget-select-form'),
 'submit',
 false
 )
 ]);
 })
 .push(function(submit_event) {
 submit_source = submit_event.target.className;
 selected_gadget = submit_event.target[0].value;
 gadget.props.content_element.querySelector("input[type=submit]")
 .disabled = true;
 if(submit_source === 'main-gadget-select-form') {
 return updateSimilarGadgetOptionList(gadget, selected_gadget);
 } else {
 return gadget.subgadget_initStateTransfer();
 }
 })
 .push(function(form_data) {
 gadget.props.content_element.querySelector("div.ui-content").innerHTML = "";
 if(submit_source === 'similar-gadget-select-form') {
 current_state = form_data;
 } 
 return declareSubGadget(gadget, selected_gadget);
 })
 .push(function() {
 return gadget.subgadget_initSubGadget();
 })
 .push(function() {
 if(submit_source === 'similar-gadget-select-form') {
 return gadget.subgadget_finishStateTransfer([current_state]);
 } else {
 return;
 } 
 })
 .push(function(){
 gadget.props.content_element.querySelector("input[type=submit]")
 .disabled = false;
 return gadgetSwitchHandler(gadget);
 });
 }

 rJS(window)

 .ready(function (g) {
 g.props = {};
 return g.getElement()
 .push(function (element) {
 $(element).trigger("create");
 g.props.element = element;
 g.props.content_element = element.querySelector('.gadget-content');
 $.getJSON("gadget_info.json", function(data) {
 var select = g.props.content_element.querySelector('form.main-gadget-select-form').main_gadget_selector,
 key;
 select.innerHTML = "";
 for(key in data) {
 var option = document.createElement("option");
 option.text = key;
 option.value = data[key];
 select.appendChild(option);
 }
 });
 });
 })
 .declareMethod('render', function () {
 var gadget = this;
 return new RSVP.Queue()
 .push(function() {
 return declareSubGadget(gadget, DEFAULT_GADGET_URL);
 })
 .push(function() {
 return gadget.subgadget_initSubGadget();
 })
 .push(function() {
 return gadgetSwitchHandler(gadget);
 });
 })

 /* ============================ SUBGADGET =============================== */
 .declareMethod("subgadget_getContent", function (param_list) {
 return this.getDeclaredGadget("sub_gadget")
 .then(function (sub_gadget) {
 return sub_gadget.getContent.apply(sub_gadget, param_list);
 });
 })
 .declareMethod("subgadget_setContent", function (param_list) {
 return this.getDeclaredGadget("sub_gadget")
 .then(function (sub_gadget) {
 return sub_gadget.setContent.apply(sub_gadget, param_list);
 });
 })
 .declareMethod("subgadget_clearContent", function (param_list) {
 return this.getDeclaredGadget("sub_gadget")
 .then(function (sub_gadget) {
 return sub_gadget.clearContent.apply(sub_gadget, param_list);
 });
 })
 .declareMethod("subgadget_initStateTransfer", function (param_list) {
 return this.getDeclaredGadget("sub_gadget")
 .then(function (sub_gadget) {
 return sub_gadget.initStateTransfer.apply(sub_gadget, param_list);
 });
 })
 .declareMethod("subgadget_finishStateTransfer", function (param_list) {
 return this.getDeclaredGadget("sub_gadget")
 .then(function (sub_gadget) {
 return sub_gadget.finishStateTransfer.apply(sub_gadget, param_list);
 });
 })
 .declareMethod("subgadget_initSubGadget", function (param_list) {
 return this.getDeclaredGadget("sub_gadget")
 .then(function (sub_gadget) {
 return sub_gadget.initSubGadget.apply(sub_gadget, param_list);
 });
 });

 }(window, document, rJS, promiseEventListener));
