describe(\'Core.getRowHeader\', function () {\n
  var id = \'testContainer\';\n
\n
  beforeEach(function () {\n
    this.$container = $(\'<div id="\' + id + \'"></div>\').appendTo(\'body\');\n
  });\n
\n
  afterEach(function () {\n
    if (this.$container) {\n
      destroy();\n
      this.$container.remove();\n
    }\n
  });\n
\n
  it(\'when not configured, should return undefined\', function () {\n
    handsontable();\n
    expect(getRowHeader(1)).toEqual(void 0);\n
  });\n
\n
  it(\'when configured as true, should return the index incremented by 1\', function () {\n
    handsontable({\n
      rowHeaders: true\n
    });\n
    expect(getRowHeader(1)).toEqual(2);\n
  });\n
\n
  it(\'when configured as array, should return value at index\', function () {\n
    handsontable({\n
      rowHeaders: [\'One\', \'Two\', \'Three\', \'Four\', \'Five\']\n
    });\n
    expect(getRowHeader(1)).toEqual(\'Two\');\n
  });\n
\n
  it(\'when configured as function, should return function output\', function () {\n
    handsontable({\n
      rowHeaders: function (index) {\n
        return \'row\' + index;\n
      }\n
    });\n
    expect(getRowHeader(1)).toEqual(\'row1\');\n
  });\n
\n
  it(\'when configured as static value, should return the value\', function () {\n
    handsontable({\n
      rowHeaders: \'static\'\n
    });\n
    expect(getRowHeader(1)).toEqual(\'static\');\n
  });\n
\n
  it(\'when configured as HTML value, should render that as HTML\', function () {\n
    handsontable({\n
      rowHeaders: function (index) {\n
        return \'<b>row\' + index + \'</b>\';\n
      }\n
    });\n
    expect(getRowHeader(1)).toEqual(\'<b>row1</b>\');\n
  });\n
\n
  it(\'when no argument given, should return as much row headers as there are rows\', function () {\n
    handsontable({\n
      rowHeaders: true,\n
      startRows: 3\n
    });\n
    expect(getRowHeader()).toEqual([1, 2, 3]);\n
  });\n
});
