describe(\'DomElement helper\', function () {\n
  //\n
  // Handsontable.helper.isInput\n
  //\n
  describe(\'isInput\', function () {\n
    it("should return true for inputs, selects, and textareas", function () {\n
      expect(Handsontable.dom.isInput(document.createElement(\'input\'))).toBe(true);\n
      expect(Handsontable.dom.isInput(document.createElement(\'select\'))).toBe(true);\n
      expect(Handsontable.dom.isInput(document.createElement(\'textarea\'))).toBe(true);\n
    });\n
\n
    it("should return true for contenteditable elements", function () {\n
      var div = document.createElement(\'div\');\n
      div.contentEditable = true;\n
      expect(Handsontable.dom.isInput(div)).toBe(true);\n
    });\n
  });\n
});\n
