describe(\'settings\', function () {\n
  describe(\'copyable\', function () {\n
    var id = \'testContainer\';\n
\n
    beforeEach(function () {\n
      this.$container = $(\'<div id="\' + id + \'"></div>\').appendTo(\'body\');\n
    });\n
\n
    afterEach(function () {\n
      if (this.$container) {\n
        destroy();\n
        this.$container.remove();\n
      }\n
    });\n
\n
    it("by default, CTRL+C should NOT copy the password value", function () {\n
      handsontable({\n
        data: [\n
          [\'Joe\', \'Secret\', \'Jack\']\n
        ],\n
        columns: [\n
          {\n
          },\n
          {\n
            type: \'password\'\n
          },\n
          {\n
          }\n
        ]\n
      });\n
\n
      expect(getCopyableData(0, 0, 0, 2)).toMatch(\'Joe\\t\\tJack\');\n
    });\n
\n
    it("with copyable=true, CTRL+C should copy the password value", function () {\n
      handsontable({\n
\n
        data: [\n
          [\'Joe\', \'Secret\', \'Jack\']\n
        ],\n
        columns: [\n
          {\n
          },\n
          {\n
            type: \'password\',\n
            copyable: true\n
          },\n
          {\n
          }\n
        ]\n
      });\n
\n
      expect(getCopyableData(0, 0, 0, 2)).toMatch(\'Joe\\tSecret\\tJack\');\n
    });\n
\n
    it("with copyable=false, CTRL+C should NOT copy the password value", function () {\n
      handsontable({\n
\n
        data: [\n
          [\'Joe\', \'Secret\', \'Jack\']\n
        ],\n
        columns: [\n
          {\n
          },\n
          {\n
            type: \'password\',\n
            copyable: false\n
          },\n
          {\n
          }\n
        ]\n
      });\n
\n
      expect(getCopyableData(0, 0, 0, 2)).toMatch(\'Joe\\t\\tJack\');\n
    });\n
\n
  });\n
});
