value.string:
module("tinymce.html.Serializer");

 test('Basic serialization', function() {
 	var serializer = new tinymce.html.Serializer();

 	expect(6);

 	equal(serializer.serialize(new tinymce.html.DomParser().parse('text\074text\046')), 'text\046lt;text\046amp;');
 	equal(serializer.serialize(new tinymce.html.DomParser().parse('\074B\076text\074/B\076\074IMG src="1.gif"\076')), '\074strong\076text\074/strong\076\074img src="1.gif" alt="" /\076');
 	equal(serializer.serialize(new tinymce.html.DomParser().parse('\074!-- comment --\076')), '\074!-- comment --\076');
 	equal(serializer.serialize(new tinymce.html.DomParser().parse('\074![CDATA[cdata]]\076')), '\074![CDATA[cdata]]\076');
 	equal(serializer.serialize(new tinymce.html.DomParser().parse('\074?xml attr="value" ?\076')), '\074?xml attr="value" ?\076');
 	equal(serializer.serialize(new tinymce.html.DomParser().parse('\074!DOCTYPE html\076')), '\074!DOCTYPE html\076');
 });

 test('Sorting of attributes', function() {
 	var serializer = new tinymce.html.Serializer();

 	expect(1);

 	equal(serializer.serialize(new tinymce.html.DomParser().parse('\074b class="class" id="id"\076x\074/b\076')), '\074strong id="id" class="class"\076x\074/strong\076');
 });
