value.string:
module("tinymce.html.Writer");

 test('Comment', function() {
 	var writer;

 	expect(2);

 	writer = new tinymce.html.Writer();
 	writer.comment('text');
 	equal(writer.getContent(), '\074!--text--\076');

 	writer = new tinymce.html.Writer();
 	writer.comment('');
 	equal(writer.getContent(), '\074!----\076');
 });

 test('CDATA', function() {
 	var writer;

 	expect(2);

 	writer = new tinymce.html.Writer();
 	writer.cdata('text');
 	equal(writer.getContent(), '\074![CDATA[text]]\076');

 	writer = new tinymce.html.Writer();
 	writer.cdata('');
 	equal(writer.getContent(), '\074![CDATA[]]\076');
 });

 test('PI', function() {
 	var writer;

 	writer = new tinymce.html.Writer();
 	writer.pi('xml', 'someval');
 	equal(writer.getContent(), '\074?xml someval?\076');

 	writer = new tinymce.html.Writer();
 	writer.pi('xml');
 	equal(writer.getContent(), '\074?xml?\076');

 	writer = new tinymce.html.Writer();
 	writer.pi('xml', 'encoding="UTF-8" \074 \076');
 	equal(writer.getContent(), '\074?xml encoding="UTF-8" \046lt; \046gt;?\076');
 });

 test('Doctype', function() {
 	var writer;

 	expect(2);

 	writer = new tinymce.html.Writer();
 	writer.doctype(' text');
 	equal(writer.getContent(), '\074!DOCTYPE text\076');

 	writer = new tinymce.html.Writer();
 	writer.doctype('');
 	equal(writer.getContent(), '\074!DOCTYPE\076');
 });

 test('Text', function() {
 	var writer;

 	expect(2);

 	writer = new tinymce.html.Writer();
 	writer.text('te\074xt');
 	equal(writer.getContent(), 'te\046lt;xt');

 	writer = new tinymce.html.Writer();
 	writer.text('');
 	equal(writer.getContent(), '');
 });

 test('Text raw', function() {
 	var writer;

 	expect(2);

 	writer = new tinymce.html.Writer();
 	writer.text('te\074xt', true);
 	equal(writer.getContent(), 'te\074xt');

 	writer = new tinymce.html.Writer();
 	writer.text('', true);
 	equal(writer.getContent(), '');
 });

 test('Start', function() {
 	var writer;

 	expect(5);

 	writer = new tinymce.html.Writer();
 	writer.start('b');
 	equal(writer.getContent(), '\074b\076');

 	writer = new tinymce.html.Writer();
 	writer.start('b', [{name: 'attr1', value: 'value1'}, {name: 'attr2', value: 'value2'}]);
 	equal(writer.getContent(), '\074b attr1="value1" attr2="value2"\076');

 	writer = new tinymce.html.Writer();
 	writer.start('b', [{name: 'attr1', value: 'val\074"ue1'}]);
 	equal(writer.getContent(), '\074b attr1="val\046lt;\046quot;ue1"\076');

 	writer = new tinymce.html.Writer();
 	writer.start('img', [{name: 'attr1', value: 'value1'}, {name: 'attr2', value: 'value2'}], true);
 	equal(writer.getContent(), '\074img attr1="value1" attr2="value2" /\076');

 	writer = new tinymce.html.Writer();
 	writer.start('br', null, true);
 	equal(writer.getContent(), '\074br /\076');
 });

 test('End', function() {
 	var writer;

 	expect(1);

 	writer = new tinymce.html.Writer();
 	writer.end('b');
 	equal(writer.getContent(), '\074/b\076');
 });

 test('Indentation', function() {
 	var writer;

 	expect(2);

 	writer = new tinymce.html.Writer({indent: true, indent_before: 'p', indent_after:'p'});
 	writer.start('p');
 	writer.start('span');
 	writer.text('a');
 	writer.end('span');
 	writer.end('p');
 	writer.start('p');
 	writer.text('a');
 	writer.end('p');
 	equal(writer.getContent(), '\074p\076\074span\076a\074/span\076\074/p\076\n\074p\076a\074/p\076');

 	writer = new tinymce.html.Writer({indent: true, indent_before: 'p', indent_after:'p'});
 	writer.start('p');
 	writer.text('a');
 	writer.end('p');
 	equal(writer.getContent(), '\074p\076a\074/p\076');
 });

 test('Entities', function() {
 	var writer;

 	expect(3);

 	writer = new tinymce.html.Writer();
 	writer.start('p', [{name: "title", value: '\074\076"\'\046\u00e5\u00e4\u00f6'}]);
 	writer.text('\074\076"\'\046\u00e5\u00e4\u00f6');
 	writer.end('p');
 	equal(writer.getContent(), '\074p title="\046lt;\046gt;\046quot;\'\046amp;\u00e5\u00e4\u00f6"\076\046lt;\046gt;"\'\046amp;\u00e5\u00e4\u00f6\074/p\076');

 	writer = new tinymce.html.Writer({entity_encoding: 'numeric'});
 	writer.start('p', [{name: "title", value: '\074\076"\'\046\u00e5\u00e4\u00f6'}]);
 	writer.text('\074\076"\'\046\u00e5\u00e4\u00f6');
 	writer.end('p');
 	equal(writer.getContent(), '\074p title="\046lt;\046gt;\046quot;\'\046amp;\046#229;\046#228;\046#246;"\076\046lt;\046gt;"\'\046amp;\046#229;\046#228;\046#246;\074/p\076');

 	writer = new tinymce.html.Writer({entity_encoding: 'named'});
 	writer.start('p', [{name: "title", value: '\074\076"\'\046\u00e5\u00e4\u00f6'}]);
 	writer.text('\074\076"\'\046\u00e5\u00e4\u00f6');
 	writer.end('p');
 	equal(writer.getContent(), '\074p title="\046lt;\046gt;\046quot;\'\046amp;\046aring;\046auml;\046ouml;"\076\046lt;\046gt;"\'\046amp;\046aring;\046auml;\046ouml;\074/p\076');
 });
