/*global window, document, rJS, RSVP, loopEventListener*/\n
/*jslint nomen: true, indent: 2, maxerr: 3 */\n
(function (window, document, rJS, RSVP, loopEventListener) {\n
  "use strict";\n
\n
  function scroll() {\n
    RSVP.Queue()\n
      .push(function () {\n
        return RSVP.delay(0);\n
      })\n
      .push(function () {\n
        window.scrollTo(0, document.body.scrollHeight || document.documentElement.scrollHeight);\n
      });\n
  }\n
\n
  rJS(window)\n
    /////////////////////////////////////////////////////////////////\n
    // ready\n
    /////////////////////////////////////////////////////////////////\n
    // Init local properties\n
    .ready(function (g) {\n
      g.props = {};\n
    })\n
\n
    // Assign the element to a variable\n
    .ready(function (g) {\n
      return g.getElement()\n
        .push(function (element) {\n
          g.props.element = element;\n
        });\n
    })\n
\n
    /////////////////////////////////////////////////////////////////\n
    // Acquired methods\n
    /////////////////////////////////////////////////////////////////\n
    .declareAcquiredMethod("updateHeader", "updateHeader")\n
    .declareAcquiredMethod("notifySubmitting", "notifySubmitting")\n
    .declareAcquiredMethod("notifySubmitted", "notifySubmitted")\n
    .declareAcquiredMethod("refresh", "refresh")\n
    .declareAcquiredMethod("jio_putAttachment", "jio_putAttachment")\n
    .declareAcquiredMethod("jio_getAttachment", "jio_getAttachment")\n
\n
    /////////////////////////////////////////////////////////////////\n
    // declared methods\n
    /////////////////////////////////////////////////////////////////\n
    .declareMethod("render", function (options) {\n
      var gadget = this,\n
        ul = document.createElement("ul");\n
      ul.setAttribute("data-role", "listview");\n
      gadget.props.jid = options.jid;\n
      return gadget.updateHeader({\n
        page_title: options.jid\n
      })\n
        .push(function () {\n
          return gadget.jio_getAttachment(options.jid, "enclosure", {format: \'text\'});\n
        })\n
        .push(function (text) {\n
          var line_list = text.split(\'\\n\'),\n
            line,\n
            tmp_line,\n
            i,\n
            index,\n
            displayed_text = \'\',\n
            is_incoming = true,\n
            is_handled;\n
\n
          function appendText(txt, incoming) {\n
            var li, pre;\n
            if (txt) {\n
              li = document.createElement("li");\n
              if (incoming) {\n
                li.setAttribute("style", "padding-right: 5em;");\n
              } else {\n
                li.setAttribute("style", "text-align: right; padding-left: 5em;");\n
              }\n
              pre = document.createElement("pre");\n
              pre.setAttribute("style", "white-space: pre-wrap;");\n
              pre.textContent = txt;\n
              li.appendChild(pre);\n
              ul.appendChild(li);\n
            }\n
          }\n
\n
          for (i = 0; i < line_list.length - 1; i += 1) {\n
            line = line_list[i];\n
            is_handled = false;\n
\n
            if (line.indexOf(\'[\') === 0) {\n
              index = line.indexOf(\'] \');\n
              if (index !== -1) {\n
                // Check direction\n
                tmp_line = line.substring(index + 2);\n
                if (tmp_line.indexOf(\'> \') === 0) {\n
                  line = tmp_line.substring(2);\n
                  // Outgoing\n
                  if (is_incoming) {\n
                    appendText(displayed_text, is_incoming);\n
                    is_incoming = false;\n
                    displayed_text = line + \'\\n\';\n
                    is_handled = true;\n
                  }\n
                } else if (tmp_line.indexOf(\'< \') === 0) {\n
                  line = tmp_line.substring(2);\n
                  // Incoming\n
                  if (!is_incoming) {\n
                    appendText(displayed_text, is_incoming);\n
                    is_incoming = true;\n
                    displayed_text = line + \'\\n\';\n
                    is_handled = true;\n
                  }\n
                }\n
              }\n
            }\n
\n
            if (!is_handled) {\n
              displayed_text += line + \'\\n\';\n
            }\n
          }\n
          appendText(displayed_text, is_incoming);\n
\n
          gadget.props.element.querySelector(".discussion-content").innerHTML = "";\n
          gadget.props.element.querySelector(".discussion-content").appendChild(ul);\n
          scroll();\n
        });\n
    })\n
\n
    .declareService(function () {\n
      var form_gadget = this;\n
\n
      function formSubmit(submit_event) {\n
        return form_gadget.notifySubmitting()\n
          .push(function () {\n
            var text = submit_event.target[0].value;\n
            submit_event.target[0].value = "";\n
            return form_gadget.jio_putAttachment(\n
              form_gadget.props.jid,\n
              \'MESSAGE\',\n
              text\n
            );\n
          })\n
          .push(function () {\n
            return form_gadget.refresh();\n
          })\n
          .push(function () {\n
            return form_gadget.notifySubmitted();\n
          })\n
          .push(undefined, function (error) {\n
            return form_gadget.notifySubmitted()\n
              .push(function () {\n
                throw error;\n
              });\n
          });\n
      }\n
\n
      // Listen to form submit\n
      return loopEventListener(\n
        form_gadget.props.element.querySelector(\'form\'),\n
        \'submit\',\n
        false,\n
        formSubmit\n
      );\n
    })\n
\n
    .declareService(function () {\n
      scroll();\n
    });\n
\n
}(window, document, rJS, RSVP, loopEventListener));
