 /*global window, rJS, Handlebars, jQuery, RSVP, loopEventListener, document, QueryFactory,
 SimpleQuery, ComplexQuery, Query*/
 (function (window, rJS, Handlebars, $, RSVP, loopEventListener) {
 "use strict";
 /////////////////////////////////////////////////////////////////
 // templates
 /////////////////////////////////////////////////////////////////
 // Precompile templates while loading the first gadget instance
 var gadget_klass = rJS(window),
 option_source = gadget_klass.__template_element
 .getElementById("option-template")
 .innerHTML,
 option_template = Handlebars.compile(option_source),

 selected_option_source = gadget_klass.__template_element
 .getElementById("selected-option-template")
 .innerHTML,
 selected_option_template = Handlebars.compile(selected_option_source),

 filter_source = gadget_klass.__template_element
 .getElementById("filter-template")
 .innerHTML,
 filter_template = Handlebars.compile(filter_source);



 function submitFilterQuery(gadget) {
 var i,
 operator,
 query = "",
 value,
 key,
 simple_query_list = [],
 complex_query,
 select,
 domain_tree_query = "",
 domain_tree_query_list = [],
 options = gadget.props.options,
 filterforms = gadget.props.element.querySelectorAll(".filterForm"),
 and = gadget.props.element.querySelector(".and");
 operator = and.checked ? "AND" : "OR";
 //build simple&complex query
 for (i = 0; i < filterforms.length; i += 1) {
 value = filterforms[i][2].value;
 if (value !== "") {
 select = filterforms[i][1][filterforms[i][1].selectedIndex].value;
 if (select === "keyword") {
 value = "%" + value + "%";
 }
 if (filterforms[i][0][filterforms[i][0].selectedIndex].value === "Searchable Text") {
 key = "";
 } else {
 key = filterforms[i][0][filterforms[i][0].selectedIndex].value;
 }
 simple_query_list.push(new SimpleQuery(
 {
 key: key,
 type: "simple",
 value: value
 }
 ));
 }
 }

 if (gadget.props.tree_path[0] !== "--") {
 for (i = 0; i < gadget.props.tree_path.length; i += 1) {
 if (gadget.props.tree_path[i] !== "--") {
 if (i === 0) {
 domain_tree_query_list.push(new SimpleQuery(
 {
 key: "category.base_category_uid",
 type: "simple",
 value: gadget.props.tree_path[i]
 }
 ));
 } else {
 domain_tree_query_list.push(new SimpleQuery(
 {
 key: "category.category_uid",
 type: "simple",
 value: gadget.props.tree_path[i]
 }
 ));
 }
 }
 }
 }
 if (simple_query_list.length > 0 || domain_tree_query_list.length > 0) {
 if (simple_query_list.length > 0) {
 complex_query = new ComplexQuery({
 operator: operator,
 query_list: simple_query_list,
 type: "complex"
 });
 query = Query.objectToSearchText(complex_query);
 }

 if (domain_tree_query_list.length > 0) {
 domain_tree_query = new ComplexQuery({
 operator: "AND",
 query_list: domain_tree_query_list,
 type: "complex"
 });
 domain_tree_query = Query.objectToSearchText(domain_tree_query);
 }

 gadget.props.jelement.panel("toggle");

 return gadget.redirect({jio_key: options.jio_key,
 "extended_search": query, "domain_tree": domain_tree_query,
 "sort_list": options.form_definition.sort_list || "",
 "hide_item": options.form_definition.hide_item || ""});
 }
 }


 function createDomainTree(gadget, options_tree_map, selected_value) {
 var select = document.createElement("select"),
 div_container = document.createElement("div"),
 domain_tree_map = gadget.props.domain_tree_map,
 domain_tree_container = gadget.props.element.querySelector(".domain_tree_container"),
 tmp = "",
 key,
 i;
 if (selected_value === undefined) {
 selected_value = "--";
 }
 if (options_tree_map) {
 domain_tree_map = options_tree_map;
 }

 function updateDomainTree(gadget, event) {
 var domain_tree_list = domain_tree_container.querySelectorAll('.domain_tree'),
 update_domain_tree_map = gadget.props.domain_tree_map;
 for (i = domain_tree_list.length - 1; i > 0; i -= 1) {
 if (domain_tree_list[i] !== event.target.parentElement.parentElement.parentElement) {
 gadget.props.tree_path.pop();
 domain_tree_list[i].parentElement.removeChild(domain_tree_list[i]);
 } else {
 break;
 }
 }
 gadget.props.tree_path[gadget.props.tree_path.length - 1] = event.target.value;
 if (event.target.value !== '--') {
 for (i = 0; i < gadget.props.tree_path.length; i += 1) {
 update_domain_tree_map = update_domain_tree_map[gadget.props.tree_path[i]];
 update_domain_tree_map = update_domain_tree_map.sub_categories;
 }
 if (Object.keys(update_domain_tree_map).length !== 0) {
 createDomainTree(gadget, update_domain_tree_map);
 }
 }
 }


 tmp += option_template({
 value: "--",
 text: "--"
 });
 for (key in domain_tree_map) {
 if (domain_tree_map.hasOwnProperty(key)) {
 if (key === selected_value) {
 tmp += selected_option_template({
 value: key,
 text: domain_tree_map[key].title
 });
 } else {
 tmp += option_template({
 value: key,
 text: domain_tree_map[key].title
 });
 }
 }
 }
 gadget.props.tree_path.push(selected_value);
 select.setAttribute("class", "domain_tree_select");
 select.innerHTML = tmp;
 div_container.setAttribute("class", "domain_tree");
 loopEventListener(
 select,
 "change",
 false,
 function (event) {
 updateDomainTree(gadget, event);
 }
 );
 div_container.appendChild(select);
 domain_tree_container.appendChild(div_container);
 $(select).selectmenu();
 }


 function createFilterItem(gadget, simple_query) {
 var div = document.createElement("div"),
 container = document.createElement("form"),
 minus_button = document.createElement("input"),
 select_reference = document.createElement("select"),
 i,
 tmp = "",
 default_select = "Searchable Text",
 select_options,
 select_options_value,
 options_map = {
 "date_float": ["Equals To", "Greater Than", "Less Than", "Not Greater Than", "Not Less Than"],
 "date_float_value": ["", ">", "<", "<=", ">="],
 "searchable_text": ["Contain"],
 "searchable_text_value": [""],
 "other": ["Exact Match", "keyword"],
 "other_value": ["", "keyword"]
 },

 text = document.createElement("input"),
 select_operator = document.createElement("select");

 if (simple_query === undefined) {
 simple_query = {};
 }

 minus_button.setAttribute("type", "button");

 container.appendChild(select_reference);
 container.appendChild(select_operator);
 container.appendChild(text);
 container.appendChild(minus_button);
 container.setAttribute("class", "filterForm");
 div.appendChild(container);
 function createSelectOperator(event) {
 var html = "",
 j;
 if (event.target.value.indexOf('date') !== -1) {
 text.setAttribute("type", "date");
 simple_query.value = simple_query.value ? simple_query.value.replace(/\//g, "-") : "";
 } else {
 text.setAttribute("type", "text");
 }
 text.setAttribute("value", simple_query.value || "");
 if (event.target.value.indexOf('date') !== -1 ||
 event.target.value.indexOf('quantity') !== -1 ||
 event.target.value.indexOf('price') !== -1) {
 select_options = options_map.date_float;
 select_options_value = options_map.date_float_value;
 } else if (event.target.value === "Searchable Text") {
 select_options = options_map.searchable_text;
 select_options_value = options_map.searchable_text_value;
 } else {
 select_options = options_map.other;
 select_options_value = options_map.other_value;
 }

 for (j = 0; j < select_options.length; j += 1) {
 if (simple_query.operator === select_options_value[j]) {
 html += selected_option_template({
 value: select_options_value[j],
 text: select_options[j]
 });
 } else {
 html += option_template({
 value: select_options_value[j],
 text: select_options[j]
 });
 }
 }
 select_operator.innerHTML = html;
 $(select_operator).selectmenu();
 $(select_operator).selectmenu('refresh');
 }


 tmp = "";
 tmp += option_template({
 value: "Searchable Text",
 text: "Searchable Text"
 });
 for (i = 0; i < gadget.props.column_list.length; i += 1) {
 if (simple_query.key === gadget.props.column_list[i][0]) {
 default_select = gadget.props.column_list[i][0];
 tmp += selected_option_template({
 value: gadget.props.column_list[i][0],
 text: gadget.props.column_list[i][1]
 });
 } else {
 tmp += option_template({
 value: gadget.props.column_list[i][0],
 text: gadget.props.column_list[i][1]
 });
 }
 }

 select_reference.innerHTML = tmp;
 createSelectOperator({"target": {"value": default_select}});

 $(select_reference).selectmenu();
 loopEventListener(
 minus_button,
 "click",
 false,
 function () {
 container.parentElement.removeChild(container);
 }
 );
 loopEventListener(
 select_reference,
 "change",
 false,
 createSelectOperator
 );
 loopEventListener(
 container,
 "submit",
 false,
 function () {
 return submitFilterQuery(gadget);
 }
 );
 return div;
 }


 gadget_klass
 /////////////////////////////////////////////////////////////////
 // ready
 /////////////////////////////////////////////////////////////////
 // Init local properties
 .ready(function (g) {
 g.props = {};
 g.props.tree_path = [];
 })

 //////////////////////////////////////////////
 // acquired method
 //////////////////////////////////////////////
 .declareAcquiredMethod("translateHtml", "translateHtml")
 .declareAcquiredMethod("redirect", "redirect")
 .declareAcquiredMethod("jio_getAttachment", "jio_getAttachment")
 // Assign the element to a variable
 .ready(function (g) {
 return g.getElement()
 .push(function (element) {
 g.props.element = element;
 g.props.jelement = $(element.querySelector("div"));
 });
 })

 .ready(function (g) {
 g.props.jelement.panel({
 display: "overlay",
 position: "right",
 theme: "c"
 });
 })

 .ready(function (g) {
 return new RSVP.Queue()
 .push(function () {
 var tmp = filter_template();
 return g.translateHtml(tmp);
 })
 .push(function (translated_html) {
 g.props.jelement.html(translated_html);
 g.props.jelement.trigger("create");
 });
 })

 /////////////////////////////////////////////////////////////////
 // declared methods
 /////////////////////////////////////////////////////////////////
 .declareMethod('toggle', function () {
 this.props.jelement.panel("toggle");
 })

 .declareMethod('render', function (options) {
 var container = this.props.element.querySelector(".filter_item_container"),
 domain_tree_container = this.props.element.querySelector(".domain_tree_container"),
 i,
 gadget = this,
 domain_tree,
 item,
 and = gadget.props.element.querySelector(".and"),
 or = gadget.props.element.querySelector(".or"),
 query = "";
 while (container.firstChild !== container.lastChild) {
 container.removeChild(container.firstChild);
 }
 while (domain_tree_container.firstChild) {
 domain_tree_container.removeChild(domain_tree_container.firstChild);
 }

 this.props.options = options;
 gadget.props.tree_path = [];
 query = options.form_definition.extended_search || "";
 domain_tree = options.form_definition.domain_tree || "";
 return new RSVP.Queue()
 .push(function () {
 return gadget.jio_getAttachment(options.jio_key, options.view);
 })
 .push(function (result) {
 var query_list,
 domain_tree_map = result._embedded._view.listbox.domain_tree_map,
 domain_tree_query_list;
 gadget.props.column_list = result._embedded._view.listbox.column_list;
 gadget.props.domain_tree_map = result._embedded._view.listbox.domain_tree_map;
 if (query !== "") {
 query_list = QueryFactory.create(query);
 if (query_list.operator === "OR") {
 or.checked = true;
 or.parentElement.children[0].setAttribute("class", "ui-btn ui-corner-all ui-btn-inherit ui-btn-icon-left ui-radio-on");
 } else {
 and.checked = true;
 and.parentElement.children[0].setAttribute("class", "ui-btn ui-corner-all ui-btn-inherit ui-btn-icon-left ui-radio-on");
 }
 query_list = query_list.query_list || [query_list];
 for (i = 0; i < query_list.length; i += 1) {
 item = createFilterItem(gadget, query_list[i]);
 container.insertBefore(item, container.lastChild);
 }
 } else {
 container.insertBefore(createFilterItem(gadget), container.lastChild);
 }

 if (domain_tree !== "") {
 domain_tree_query_list = QueryFactory.create(domain_tree);
 domain_tree_query_list = domain_tree_query_list.query_list || [domain_tree_query_list];
 for (i = 0; i < domain_tree_query_list.length; i += 1) {
 if (Object.keys(domain_tree_map).length !== 0) {
 createDomainTree(gadget, domain_tree_map, domain_tree_query_list[i].value);
 }
 domain_tree_map = domain_tree_map[domain_tree_query_list[i].value].sub_categories;
 }
 if (Object.keys(domain_tree_map).length !== 0) {
 createDomainTree(gadget, domain_tree_map, "--");
 }
 } else {
 createDomainTree(gadget);
 }
 });
 })

 /////////////////////////////////////////////////////////////////
 // declared services
 /////////////////////////////////////////////////////////////////
 .declareService(function () {
 var gadget = this,
 item;
 function closeFilterEditor() {
 gadget.props.jelement.panel("toggle");
 }


 loopEventListener(
 gadget.props.element.querySelector(".plus"),
 "submit",
 false,
 function () {
 var container = gadget.props.element.querySelector(".filter_item_container");
 item = createFilterItem(gadget);
 container.insertBefore(item, container.lastChild);
 }
 );
 loopEventListener(
 gadget.props.element.querySelector(".delete"),
 "submit",
 false,
 closeFilterEditor
 );
 loopEventListener(
 gadget.props.element.querySelector(".submit"),
 "submit",
 false,
 function () {
 return submitFilterQuery(gadget);
 }
 );
 });

 }(window, rJS, Handlebars, jQuery, RSVP, loopEventListener));
