function callBeforeRequest(type){
 switch(type){
 case 1:
 message = "Loading Events...";
 break;
 case 2:
 message = "Adding Event...";
 break;
 case 3:
 message = "Removing Event...";
 break;
 case 4:
 message = "The request is being processed ...";
 break;
 }
 $("#errorpannel").hide();
 $("#loadingpannel").html(message).show();
 }

 function callAfterRequest(type){
 switch(type){
 default:
 $("#loadingpannel").hide();
 break;
 }
 }

 function callOnError(type, data){
 $("#errorpannel").show();
 }

 function Edit(data){
 var url ="WebSection_newEvent";
 var editUrl="WebSection_newEvent?id={0}&start={2}&end={3}&isallday={4}&title={1}";
 if(data){
 var url = StrFormat(editUrl, data);
 OpenModelWindow(url,{width: 400, height: 200, caption:"Manage The Event", onclose:function(){
 $("#gridcontainer").reload();
 }});
 }
 }

 function View(data){
 var str = "";
 $.each(data, function(i, item){
 str += "[" + i + "]: " + item + "\n";
 });
 alert(str);
 }
 function Delete(data, callback){
 hiConfirm("Are You Sure to Delete this Event", 'Confirm', function(r){ r && callback(0);});
 }

 function wtd(p){
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $("#showdaybtn").addClass("fcurrent");
 }

 $(document).ready(function() { 
 var DATA_FEED_URL = "Base_updateCalendarEventList";
 var op = {
 view: "week",
 showday: new Date(),
 EditCmdhandler:Edit,
 DeleteCmdhandler:Delete,
 weekstartday: 0,
 ViewCmdhandler:View,
 onWeekOrMonthToDay:wtd,
 onBeforeRequestData: callBeforeRequest,
 onAfterRequestData: callAfterRequest,
 onRequestDataError: callOnError,
 autoload:true,
 url: DATA_FEED_URL + "?request_type=list",
 quickAddUrl: DATA_FEED_URL + "?request_type=add",
 quickUpdateUrl: DATA_FEED_URL + "?request_type=update",
 quickDeleteUrl: DATA_FEED_URL + "?request_type=remove",
 };
 var $dv = $("#calhead");
 var _MH = document.documentElement.clientHeight;
 var dvH = $dv.height() + 2;
 op.height = _MH - dvH;
 op.eventItems =[];
 var p = $("#gridcontainer").bcalendar(op).BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 $("#caltoolbar").noSelect();
 //to show day view
 $("#showdaybtn").click(function(e) {
 //document.location.href="#day";
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $(this).addClass("fcurrent");
 var p = $("#gridcontainer").swtichView("day").BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //to show week view
 $("#showweekbtn").click(function(e) {
 //document.location.href="#week";
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $(this).addClass("fcurrent");
 var p = $("#gridcontainer").swtichView("week").BcalGetOp();
 if (p && p.datestrshow){
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //to show month view
 $("#showmonthbtn").click(function(e) {
 //document.location.href="#month";
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $(this).addClass("fcurrent");
 var p = $("#gridcontainer").swtichView("month").BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 $("#showreflashbtn").click(function(e){
 $("#gridcontainer").reload();
 }); 
 //Add a new event
 $("#faddbtn").click(function() {
 var url ="WebSection_newEvent";
 $("div#new_event_dialog").load(url);
 $("div#new_event_dialog").dialog("open");
 });
 //go to today
 $("#showtodaybtn").click(function() {
 var p = $("#gridcontainer").gotoDate().BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //previous date range
 $("#sfprevbtn").click(function() {
 var p = $("#gridcontainer").previousRange().BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //next date range
 $("#sfnextbtn").click(function() {
 var p = $("#gridcontainer").nextRange().BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 $("div#new_event_dialog").dialog({
 autoOpen: false,
 height: 192,
 width: 410,
 modal: true,
 buttons: {
 "Create": function(){
 $.post("EventModule_createNewEvent",
 $("form#create_new_event").serialize(),
 function(){
 $("div#new_event_dialog").dialog("close");
 $("div#showreflashbtn.fbutton").click();
 }
 );
 }
 },
 });
 $( "#datepicker" ).datepicker({
 onSelect: function(dateText, inst){
 var dateList = dateText.split("/");
 var month = dateList[0] - 1;
 var day = dateList[1];
 var year = dateList[2];
 var p = $("#gridcontainer").gotoDate(new Date(year, month, day)).BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 }
 });
 });
