function callBeforeRequest(type){
 var message = "Loading data...";
 switch(type){
 case 1:
 message = "Loading data...";
 break;
 case 4:
 message = "The request is being processed ...";
 break;
 }
 $("#errorpannel").hide();
 $("#loadingpannel").html(message).show();
 }

 function callAfterRequest(type){
 switch(type){
 case 1:
 $("#loadingpannel").hide();
 break;
 case 4:
 $("#loadingpannel").html("Success!");
 window.setTimeout(function(){$("#loadingpannel").hide();},2000);
 break;
 }
 }

 function callOnError(type, data){
 $("#errorpannel").show();
 }

 function Edit(data){
 var editUrl="WebSection_newEvent?id={0}&start={2}&end={3}&isallday={4}&title={1}";
 if(data){
 var url = StrFormat(editUrl, data);
 OpenModelWindow(url,{width: 600, height: 400, caption:"Manage The Event",onclose:function(){
 $("#gridcontainer").reload();
 }});
 }
 }

 function View(data){
 var str = "";
 $.each(data, function(i, item){
 str += "[" + i + "]: " + item + "\n";
 });
 alert(str);
 }
 function Delete(data, callback){
 $.alerts.okButton = "Ok"; 
 $.alerts.cancelButton = "Cancel";
 hiConfirm("Are You Sure to Delete this Event", 'Confirm', function(r){ r && callback(0);});
 }

 function wtd(p){
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $("#showdaybtn").addClass("fcurrent");
 }

 $(document).ready(function() { 
 var DATA_FEED_URL = "Base_getEventListSerialized";
 var op = {
 view: "day",
 showday: new Date(),
 EditCmdhandler:Edit,
 DeleteCmdhandler:Delete,
 weekstartday: 0,
 ViewCmdhandler:View,
 onWeekOrMonthToDay:wtd,
 onBeforeRequestData: callBeforeRequest,
 onAfterRequestData: callAfterRequest,
 onRequestDataError: callOnError,
 autoload:true,
 url: DATA_FEED_URL + "?request_type=list",
 quickAddUrl: DATA_FEED_URL + "?request_type=add",
 quickUpdateUrl: DATA_FEED_URL + "?request_type=update",
 quickDeleteUrl: DATA_FEED_URL + "?requet_type=remove",
 };
 var $dv = $("#calhead");
 var _MH = document.documentElement.clientHeight;
 var dvH = $dv.height() + 2;
 op.height = _MH - dvH;
 op.eventItems =[];
 var p = $("#gridcontainer").bcalendar(op).BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 $("#caltoolbar").noSelect();
 $("#hdtxtshow").datepicker({ picker: "#txtdatetimeshow", showtarget: $("#txtdatetimeshow"),
 onReturn:function(r){ 
 var p = $("#gridcontainer").gotoDate(r).BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 } 
 });
 //to show day view
 $("#showdaybtn").click(function(e) {
 //document.location.href="#day";
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $(this).addClass("fcurrent");
 var p = $("#gridcontainer").swtichView("day").BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //to show week view
 $("#showweekbtn").click(function(e) {
 //document.location.href="#week";
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $(this).addClass("fcurrent");
 var p = $("#gridcontainer").swtichView("week").BcalGetOp();
 if (p && p.datestrshow){
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //to show month view
 $("#showmonthbtn").click(function(e) {
 //document.location.href="#month";
 $("#caltoolbar div.fcurrent").each(function() {
 $(this).removeClass("fcurrent");
 })
 $(this).addClass("fcurrent");
 var p = $("#gridcontainer").swtichView("month").BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 $("#showreflashbtn").click(function(e){
 $("#gridcontainer").reload();
 }); 
 //Add a new event
 $("#faddbtn").click(function(e) {
 var url ="WebSection_newEvent";
 OpenModelWindow(url,{ width: 500, height: 400, caption: "Create New Event"});
 });
 //go to today
 $("#showtodaybtn").click(function(e) {
 var p = $("#gridcontainer").gotoDate().BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //previous date range
 $("#sfprevbtn").click(function(e) {
 var p = $("#gridcontainer").previousRange().BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 //next date range
 $("#sfnextbtn").click(function(e) {
 var p = $("#gridcontainer").nextRange().BcalGetOp();
 if (p && p.datestrshow) {
 $("#txtdatetimeshow").text(p.datestrshow);
 }
 });
 });
