value.string:
request = context.REQUEST

 def getRequestKeys(rq):
 return map(lambda x: x[0], rq.items())

 def getRequestValues(rq):
 return map(lambda x: x[1], rq.items())

 def getSpreadsheet():
 from Products.ERP5OOo.OOoUtils import OOoParser
 OOoParser = OOoParser()
 # Extract tables from the speadsheet file
 import_file = kw['import_file']
 OOoParser.openFile(import_file)
 return OOoParser.getSpreadsheetsMapping()


 # if listbox is empty, then we are in the first step
 listbox = []
 if kw.has_key('listbox') and kw['listbox'] not in ('', None):
 listbox = kw['listbox']


 # First step
 if len(listbox) == 0:

 spreadsheets = getSpreadsheet()
 # Put the result of OOo parsing in the request
 request.set('ooo_import_spreadsheet_data', spreadsheets)


 # Second Step
 else:

 # Re-upload the OOo file
 spreadsheets = getSpreadsheet()

 # Build the data mapping
 mapping = {}
 request.set('ooo_import_spreadsheet_data', spreadsheets)
 listbox_ordered_lines = context.ERP5Site_getSpreadsheetColumnNameList()
 for line in listbox_ordered_lines:
 listbox_id = line.getUid()[4:]
 portal_type_property = listbox[listbox_id]['portal_type_property_list']
 if portal_type_property not in ('', None):
 spreadsheet_name = getattr(line, 'spreadsheet_name')
 column_name = getattr(line, 'spreadsheet_column')
 map_item = { 'portal_type': portal_type_property.split('.')[0]
 , 'property' : portal_type_property.split('.')[1]
 }
 if spreadsheet_name not in mapping.keys():
 mapping[spreadsheet_name] = {}
 mapping[spreadsheet_name][column_name] = map_item

 # Convert each spreadsheet
 for (sheet_name, sheet_data) in spreadsheets.items():

 # The ERP5 module where all lines of the current spreadsheet will be generated
 sheet_module = context

 # Build a data structure to associate column index with column title
 column_index = {}
 for column_id in range(len(sheet_data[0])):
 column_index[column_id] = sheet_data[0][column_id]

 # Build a data structure to associate column index with object property and portal type
 column_mapping = {}
 for (column_name, property_dict) in mapping[spreadsheet_name].items():
 for (column_id, column_title) in column_index.items():
 if column_name == column_title:
 column_mapping[column_id] = property_dict
 break

 # Create a dict to describe each line in property
 object_list = {}
 for line in sheet_data[1:]:
 new_object_property_dict = {}
 for line_property_index in range(len(line)):
 if line_property_index in column_mapping.keys():
 property_module = sheet_module
 property_pt = column_mapping[line_property_index]['portal_type']
 property_id = column_mapping[line_property_index]['property']
 property_value = line[line_property_index]

 if not property_value in ('', None):
 if not object_list.has_key(property_module):
 object_list[property_module] = {}
 if not object_list[property_module].has_key(property_pt):
 object_list[property_module][property_pt] = []

 # Create a new property value
 new_object_property_dict[property_id] = property_value

 if len(new_object_property_dict.keys()) > 0:
 object_list[property_module][property_pt].append(new_object_property_dict)

 # Create new ERP5 objects
 new_path_list = []
 for (module, per_portal_type_data) in object_list.items():
 for (portal_type, new_object_list) in per_portal_type_data.items():
 for new_object_property_dict in new_object_list:
 new_object = module.newContent( portal_type = portal_type
 , immediate_reindex = 1
 )
 new_object.edit(**new_object_property_dict)
 new_path_list.append(new_object.getRelativeUrl())

 return request.RESPONSE.redirect(context.absolute_url() + '/person_module/view?portal_status_message=OpenOffice+document+successfully+imported.+New+object+created:+' + str(len(new_path_list)))

 return context.ERP5Site_importObjectFromOOoFastInput(REQUEST=request)
