 function submitAction(form,act) {
 form.action = act;
 form.submit();
 }

 function fixLeftRightHeight(){
 var lh=document.getElementById('fieldset_left').offsetHeight;
 var rh=document.getElementById('fieldset_right').offsetHeight;
 document.getElementById('fieldset_left').style.height=(lh>rh)? lh+"px" : rh+"px";
 document.getElementById('fieldset_right').style.height=(lh>rh)? lh+"px" : rh+"px";
 document.getElementById('fieldset_left').style.borderTop = '1px solid #3D7474'
 document.getElementById('fieldset_left').style.borderLeft = '1px solid #3D7474'
 document.getElementById('fieldset_left').style.borderBottom = '1px solid #3D7474'
 document.getElementById('fieldset_right').style.borderTop = '1px solid #3D7474'
 document.getElementById('fieldset_right').style.borderRight = '1px solid #3D7474'
 document.getElementById('fieldset_right').style.borderBottom = '1px solid #3D7474'
 }
