 function submitAction(form,act) {
 form.action = act;
 form.submit();
 }

 function fixLeftRightHeight(){
 var lh = 0;
 var lfieldset;
 var rh = 0;
 var rfieldset;
 var list=document.getElementsByTagName('fieldset');
 for(i=0; i<list.length; i=i+1){
 list_parts = list[i].id.split('_');
 for(j=1; j<list_parts.length; j=j+1){
 if(list_parts[j] == "left"){
 lfieldset = list[i];
 lh = lfieldset.offsetHeight;
 break;
 }else{
 if(list_parts[j] == "right"){
 rfieldset = list[i];
 rh = rfieldset.offsetHeight;
 break;
 }
 }
 }
 if(lh && rh){
 break;
 }
 }
 if(lh && rh){
 lfieldset.style.height = (lh>rh)? lh+"px" : rh+"px";
 rfieldset.style.height = (lh>rh)? lh+"px" : rh+"px";
 lfieldset.style.borderRightWidth = 0;
 rfieldset.style.borderLeftWidth = 0;
 }
 }
